import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props)


    this.state = {
      newItem: "",
      list: []
    }

  }

  deleteItem(id) {

  }
  updateInput(key, value) {
    // update react state here
    this.setState({
      [key]: value
    })
  }

  addItem() {
    //create item with unigue id
    const newItem = {
      id: 1 + Math.random(), // when is comma important ?
      value: this.state.newItem.slice() // when : and when = ;
    }


    // copy current list of items
    const list = [...this.state.list];

    // add new item to list
    list.push(newItem);

    //update state with new list and reset newitem input
    this.setState({
      list,
      newItem: ""
    }); // when is ; necessary ?



  }

  render() {
    return (<div className="App" >
      <div>
        add an item
              <br />
        <input
          type="text"
          placeholder="type item in here"
          value={this.state.newItem}
          onChange={e => this.updateInput("newItem", e.target.value)}
        />
        <button onClick={() => this.addItem()}>
          ADD ITEM
              </button>
        <br />
        <ul>
          {this.state.list.map(item => {
            return (
              <li key={item.id}>
                {item.value}
                <button
                  onclick={() => this.deleteItem(item.id)}>
                  delete item 
               </button>
              </li>
            )
          })}
        </ul>
      </div>
    </div>
    );
  }
}

export default App;
