import React, { Component } from 'react';
import './App.css';


class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      noteText: "",

    }

  }





  render() {
    return (
      <div className="container" >
        <div className="header" > ToDo App </div>
        <div className="btn" > + </div>
        <input
          type="text"
          ref={((input) => { this.textInput = input })}
          className = "textInput"
          value = {this.state.noteText}
        >

        </input>


      </div>
    );
  }
}

export default App;
